//Breaking down flight path into sequence of instructions
let instructions = flightPath.split(" ");


//placing drone on the map
let drone = {
  x: 0,
  y: 0,
  compass: 'N',
  thrust: 1
}
console.log(`Take off! Drone begins the flight from position ${drone.x},${drone.y}`);

const leftTurn = () => {
  switch (drone.compass) {
    case 'W':
      drone.compass = 'S';
      drone.thrust = -1;
      break;
    case 'S':
      drone.compass = 'E';
      drone.thrust = 1;
      break;
    case 'E':
      drone.compass = 'N';
      drone.thrust = 1;
      break;
    default:
      drone.compass = 'W';
      drone.thrust = -1;
      break;
  }
  // return drone.thrust
};

const rightTurn = () => {
  switch (drone.compass) {
    case 'W':
      drone.compass = 'N';
      drone.thrust = 1;
      break;
    case 'S':
      drone.compass = 'W';
      drone.thrust = -1;
      break;
    case 'E':
      drone.compass = 'S';
      drone.thrust = -1;
      break;
    default:
      drone.compass = 'E';
      drone.thrust = 1;
      break;
  }
  // return drone.thrust

}

const stepsValue = (m) => {
  // number of steps in a move
  let v = 0
  for (let i = 1; i < instructions[m].length; i++) {
    if (instructions[m].charAt(i) == '+') {
      v += 1;
    } else if (instructions[m].charAt(i) == '-') {
      v -= 1;
    }
  };
  //move direction
  if (instructions[m].charAt(0) == 'L') {
    leftTurn();
  } else if (instructions[m].charAt(0) == 'R') {
    rightTurn();
  };
  return v * drone.thrust
};

const moveDrone = () => {
  for (let i = 0; i < instructions.length; i++) {
    let step = stepsValue(i);
    if (drone.compass == 'W' || drone.compass == 'E') {
      drone.x += step
    } else {
      drone.y += step;
    }
  }
  console.log(`Game Over. You safely arrived at your final destination : ${drone.x},${drone.y}`);
}

moveDrone();